import React, { useState } from 'react';
import { connect } from 'react-redux';
import { addReminder } from '../actions/actionCreators';
import { bindActionCreators } from 'redux';


function AddReminder(props) {
	const [remindertext, setReminderText] = useState('');

	return (
		<div className="form-group row">
			<div className="col-sm-10">
				<input onChange={e => setReminderText(e.target.value)} value={remindertext} type="text" className="form-control" id="reminder" placeholder="Add Reminder Here" />
				<button type="button" onClick={() => setReminderText('')} style={{ marginTop: '25px', marginRight: '15px' }} className="btn btn-danger">Cancel</button>
				<button type="button" onClick={() => { props.addReminder(remindertext); setReminderText(''); }} style={{ marginTop: '25px' }} className="btn btn-success">Add Reminder</button>
			</div>
		</div>
	)
}

const mapStateToProps = state => {
	console.log('2', state);
	return state;
};

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		addReminder
	}, dispatch);
}

export default connect(null, mapDispatchToProps)(AddReminder);
