	return (
		<div className="form-panel">
			<div className="form-panel__input">
				<input onChange={e => setReminderText(e.target.value)} value={remindertext} type="text" className="form-panel__input-text" id="reminder" placeholder="Add Reminder Here" />
				<button type="button" onClick={() => setReminderText('')} className="form-panel__input-cross">Cancel</button>
			</div>
			<button type="button" onClick={() => { props.addReminder(remindertext); setReminderText(''); }} className="form-panel__input-add">Add Reminder</button>
		</div>
	)
}

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		addReminder
	}, dispatch);
}

export default connect(null, mapDispatchToProps)(AddReminder);
