import { ADD_REMINDER, REMOVE_REMINDER, TOGGLE_REMINDER } from '../actions/actionTypes';

const INITIAL_REMINDER = [];

const AddReminderReducer = (state = INITIAL_REMINDER, action) => {
	// console.log('actions detail', action); 
	switch (action.type) {
		case ADD_REMINDER:
			return [...state, {
				id: action.payload.id,
				reminderText: action.payload.reminderText,
				completed: false,
			}];

		case REMOVE_REMINDER:
			const index = parseInt(action.payload.id);
			return state.filter(todo => todo.id !== index);

		case TOGGLE_REMINDER:
			return state.map(todo => (todo.id === action.payload.id) ? 
			{...todo, completed: !todo.completed}: todo)

		default:
			return state;
	}
}

export default AddReminderReducer;
