import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { showOnlineReminder } from '../actions/actionCreators';


function OnlineReminders(props) {
	const [onlineList, setOnlineList] = useState([]);
	const [error, setError] = useState(false);
	const [isLoading, setIsLoading] = useState(false);
	const [isShown, setIsShown] = useState(false);

	async function fetchData() {
		await fetch("https://jsonplaceholder.typicode.com/todos")
			.then(res => res.json())
			.then(response => {
				props.showOnlineReminder(getUnique(response, 'userId'));
				// setOnlineList(getUnique(response, 'userId'));
				setIsLoading(false);
			})
			.catch(setError(error));
	}

	function getUnique(arr, comp) {
		const unique = arr
			.map(e => e[comp])
			.map((e, i, final) => final.indexOf(e) === i && i)
			.filter(e => arr[e]).map(e => arr[e]);
		return unique;
	}

	useEffect(() => {
		if (isShown){
			setIsLoading(true)
		  fetchData();
		}
	}, []);

	return (
		<div className="reminder-container">
			<a href="/#" onClick={() => setIsShown(true)}>Click here</a>
			{isShown && <h4>Here are the list of available random reminders below:</h4>}
			{isLoading && <p>Wait!!! Loading reminders for you ...</p>}
			<ul className="reminder-list">
				{props.onlineReminderList[0] && props.onlineReminderList[0].list.map(listItem => (
					<li key={listItem.userId}>
						<p className="listItem">{listItem.title}</p>
						<div className="actions">
							<button type="button" className="btn btn-status">{listItem.completed ? 'COMPLETED' : 'INPROGRESS'}</button>
						</div>
					</li>
				)
				)}
			</ul>
		</div>
	);
}

const mapStateToProps = state => ({
	onlineReminderList: state.OnlineReminderReducer
})

const mapDispatchToProps = dispatch => {
	return bindActionCreators({
		showOnlineReminder
	}, dispatch);
};

export default connect(mapStateToProps, mapDispatchToProps) (OnlineReminders);
