import { ADD_REMINDER, REMOVE_REMINDER, SHOW_ALL} from './actionTypes';

let todoId = 11111;

export const addReminder = reminderText => ({
	type: ADD_REMINDER,
	payload: {
		id: todoId++,
		reminderText
	}
});

export const deleteReminder = (id) => ({
	type: REMOVE_REMINDER,
	payload: {
		id
	}
});

export const showOnlineReminder = (list) => ({
	type: SHOW_ALL,
	payload: list
})
