import { ADD_REMINDER, REMOVE_REMINDER } from './actionTypes';

let todoId = 11111;

export const addReminder = reminderText => ({
	type: ADD_REMINDER,
	payload: {
		id: todoId++,
		reminderText
	}
});

export const deleteReminder = (id) => ({
	type: REMOVE_REMINDER,
	payload: {
		id
	}
});
