import { SHOW_ALL, SHOW_ACTIVE, SHOW_COMPLETED} from '../actions/actionTypes';

const OnlineReminderReducer = (state = {}, action) => {
	switch (action.type) {
		case SHOW_ALL:
			return {...state, list:action.payload.list};

		case SHOW_ACTIVE:
			console.log('asdasda', state);
			return {...state, list:state[0].filter(item => !item.completed)};

		case SHOW_COMPLETED:
			return {...state, list:state[0].filter(item => item.completed)};

		default:
			return state;
	}
}

export default OnlineReminderReducer;
