import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { deleteReminder, toggleReminder } from '../actions/actionCreators';

function ListReminders(props) {
	return (
		<div className="reminder-container">
			<ul className="reminder-list">
				{props.reminderList.map(reminder => {
					return <li key={reminder.id}>{reminder.reminderText}</li>
				})}
			</ul>
		</div>
	)
}

const mapStateToProps = state => ({
	reminderList: state.AddReminderReducer //need to change the name later
})

// const mapStateToProps = state => ({
//     todos: state.todos
// })

// const mapStateToProps = state => { 
//     return { todos: state.todos }; 
// };

const mapDispatchToProps = dispatch => {
	return bindActionCreators({
		deleteReminder,
		toggleReminder
	}, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(ListReminders);
