import React from 'react';
import { connect } from 'react-redux';
import { deleteReminder } from '../actions/actionCreators';
import { bindActionCreators } from 'redux';
//import ListItemReminder from './ListItemReminder';

function ListReminders({props}) {
	const {reminderList} = props;
	return (
		reminderList.length !== 0 ?
			<div className="reminder-container">
				<ul className="reminder-list">
					{reminderList.map(reminder => {
						return (
							<li key={reminder.id}>
								<p className="listItem">{reminder.reminderText}</p>
								<div className="actions">
									<button type="button" className="btn btn-warning">Edit</button>
									<button type="button" className="btn btn-delete" 
									  onClick={()=> props.deleteReminder(reminder.id)}>Delete</button>
								</div>
							</li>
						)
						// return <ListItemReminder reminder={}/>
					})}
				</ul>
			</div> : null
	)
}

const mapStateToProps = state => ({
	reminderList: state.AddReminderReducer //need to change the name later
})

const mapDispatchToProps = (dispatch) => {
	return bindActionCreators({
		deleteReminder
	}, dispatch);
}
// const mapStateToProps = state => ({
//     todos: state.todos
// })

// const mapStateToProps = state => { 
//     return { todos: state.todos }; 
// };

export default connect(mapStateToProps, mapDispatchToProps)(ListReminders);
