import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { deleteReminder, toggleReminder } from '../actions/actionCreators';

function ListReminders(props) {
	console.log('here it is', props);
	return(
		<div>
		{props.reminderList.map(reminder => {
			return  <h1>{reminder.reminderText}</h1>
		})}
		</div>
	)
}

const mapStateToProps = state => ({
	reminderList: state.AddReminderReducer
})

// const mapStateToProps = state => ({
//     todos: state.todos
// })

// const mapStateToProps = state => { 
//     return { todos: state.todos }; 
// };

const mapDispatchToProps = dispatch => {
	return bindActionCreators({
		deleteReminder, 
		toggleReminder
	}, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(ListReminders);
