import { ADD_REMINDER, REMOVE_REMINDER, TOGGLE_REMINDER } from './actionsTypes';

let todoId = 11111;

export const addReminder = text => ({
	type: ADD_REMINDER,
	payload: {
		id: todoId++,
		text
	}
});

export const deleteReminder = (id) => ({
	type: REMOVE_REMINDER,
	payload: {
		id
	}
});

export const toggleReminder = (id) => ({
	type: TOGGLE_REMINDER,
	payload: {
		id
	}
});
