import { SHOW_ALL, SHOW_ACTIVE, SHOW_COMPLETED} from '../actions/actionTypes';

const OnlineReminderReducer = (state = [], action) => {
	switch (action.type) {
		case SHOW_ALL:
			return action.payload.list;

		case SHOW_ACTIVE:
			console.log('check state content', state)
			return state.filter((item) => !item.completed);

		case SHOW_COMPLETED:
			console.log('check state content 123', state)
			return state.filter((item) => item.completed);

		default:
			return state;
	}
}

export default OnlineReminderReducer;
