import React from 'react';
import { connect } from 'react-redux';
import { addReminder } from '../actions/actionCreators'
import {bindActionCreators} from 'redux'


class AddReminder extends React.Component {

	constructor(props){
		super(props);
		console.log(props);
		this.state = {
			remindertext: '',           
		};
		this.onChangeReminderText = this.onChangeReminderText.bind(this);
	}

	onChangeReminderText(e){
		this.setState({
			remindertext: e.target.value
		});
	}

	render() {
		return (
			<div className="form-group row">
				<div className="col-sm-10">
					<input onChange={this.onChangeReminderText} value={this.state.remindertext} type="text" className="form-control" id="reminder" placeholder="Add Reminder Here" />
					<button type="button" onClick={() => this.setState({ remindertext: '' })} style={{ marginTop: '25px', marginRight: '15px' }} className="btn btn-danger">Cancel</button>
					<button type="button" onClick={() => { this.props.addReminder(this.state.remindertext); this.setState({ remindertext: '' }); }} style={{ marginTop: '25px' }} className="btn btn-success">Add Reminder</button>
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => {
	console.log(state);
	return state;
};

const mapDispatchToProps = (dispatch)  => {
	return bindActionCreators({
    addReminder
	}, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps) (AddReminder);
