import { SHOW_ALL, SHOW_ACTIVE, SHOW_COMPLETED, TOGGLE_REMINDER} from '../actions/actionTypes';

const OnlineReminderReducer = (state = [], action) => {
	switch (action.type) {
		case SHOW_ALL:
			return [...state, {
				list: action.payload.list
			}]

		case SHOW_ACTIVE:
			return state.filter((item) => !item.completed)

		case SHOW_COMPLETED:
			return state.filter((item) => item.completed);

		default:
			return state;
	}
}

export default OnlineReminderReducer;
